import React, { useContext, useEffect, useState } from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Button from '@mui/material/Button';
import { Link } from 'react-router-dom';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import './navigationBar.css'
import MenuIcon from '@mui/icons-material/Menu';
import { styled, alpha } from '@mui/material/styles';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';
import { AuthContext } from './AuthContext';

const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  '&:hover': {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginRight: theme.spacing(2),
  marginLeft: 0,
  width: '100%',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(3),
    width: 'auto',
  },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '30ch',
    },
  },
}));
const NavigationBar = () => {
  const { isLoggedIn,logout,isAdmin } = useContext(AuthContext);

  return (
    <div className='header'>
    <AppBar position="static" sx={{bgcolor:"#3f51b5"}}>
            <Toolbar sx={{minHeight:'none'}}>
                <ShoppingCartIcon
                    size="small"
                    edge="start"
                    color="inherit"
                    aria-label="menu"
                    sx={{ mr: 1 }}
                >
                    <MenuIcon />
                </ShoppingCartIcon>
                <h5 className='title'>upGrad E-Shop</h5>
                  {isLoggedIn?
                    <div className='login'>
                    <Link className='title' to={'/Login'}>Login</Link>
                    <Link className='title' to={'/SignUp'}>SignUp</Link></div>
                 : <>
                  <Search sx={{marginLeft:"-20"}}>
                 <SearchIconWrapper>
                   <SearchIcon />
                 </SearchIconWrapper>
                 <StyledInputBase
                   placeholder="Searchâ€¦"
                   inputProps={{ 'aria-label': 'search' }}
                 />
               </Search>
               <div style={{float:'right',width:'100%',position: 'relative'}}>
               <Link className='title' to={'/Home'}>Home</Link>
                <Link className='title' to={'/AddProducts'}>SignUp</Link>
                <Button className='btn' variant="contained" color="inherit" sx={{bgcolor:"#f50057", m:"2"}} >Logout</Button>
                </div></>
                } </Toolbar>
        </AppBar>
        </div>
  );
};

export default NavigationBar;
