import React, { useEffect, useState } from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { ButtonGroup,IconButton } from "@mui/material";
import { Delete, Edit } from "@mui/icons-material";

const Product = (props) => {
  const [isAdmin, setIsAdmin] = useState(false);
  const path = require(props.prod.imgUrl,toString());
  useEffect(() => {
    let flagisadmin = localStorage.getItem("isAdmin");
    if (flagisadmin === "true") {
      setIsAdmin(true);
    }
  });

  return (
    <Card
      sx={{ maxWidth: 320, margin: 0.85, padding: 1 }}
      key={props.prod.productid}
    >
      <CardMedia
        component="img"
        alt="green iguana"
        height="200"
        image= {path}
      />
      <CardContent>
        <Typography gutterBottom variant="h6" component="div">
          {props.prod.prodName} <b style={{justifyContent:'end'}}>{props.prod.price}</b>
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {props.prod.Description}
        </Typography>
      </CardContent>
      <CardActions sx={{alignSelf: 'flex-end' }}>
        <Button size="small" variant="contained" sx={{ bgcolor: "#3f51b5"}}>
          Buy
        </Button>
        {isAdmin && (
          <ButtonGroup aria-label="outlined primary button group" sx={{margin:'auto',marginRight:0}}>
            <IconButton>
              <Edit />
            </IconButton>
            <IconButton>
              <Delete />
            </IconButton>
          </ButtonGroup>
        )}
      </CardActions>
    </Card>
  );
};

export default Product;
