import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { ButtonGroup } from '@mui/material';

const Product=(props)=> {
  const [isAdmin,setIsAdmin]=useState(false);
  useEffect(() => {
    let flagisadmin = localStorage.getItem("isAdmin");
    if (flagisadmin === "true") {
      setIsAdmin(true);
    }
  });
    const path='';
    return (
      <Card sx={{ maxWidth: 340,margin:0.85,padding:1}} key={props.prod.productid}>
        <CardMedia
          component="img"
          alt="green iguana"
          height="140"
          image={props.prod.imgUrl}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {props.prod.prodName} rs-{props.prod.price}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {props.prod.Description}
          </Typography>
        </CardContent>
        <CardActions>
          <Button size="small" variant='contained' sx={{bgcolor:"#3f51b5"}}>Buy</Button>   
          {isAdmin &&
          <ButtonGroup>
          <Button size="small">Learn More</Button>
          <Button size="small">Learn More</Button>
          </ButtonGroup>}
        </CardActions>
      </Card>
    );
  }



  export default  Product;
