   <Box sx={{ minWidth: 275 }}>
    <Card
      sx={{ maxWidth: 320, margin: 0.85, padding: 1 }}
      key={props.prod.productid} variant="outlined">
      <CardMedia
        component="img"
        alt="green iguana"
        height="200"
        image={props.prod.imgUrl}
        className="prodimg"
      />
      <CardContent>
        <Typography gutterBottom variant="h6" component="div">
          {props.prod.prodName} <b style={{margin:'auto',marginRight:'0'}}>â‚¹ {props.prod.price}</b>
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {props.prod.Description}
        </Typography>
      </CardContent>
      <CardActions>
        <div className="btnbottom">
        <Button size="small" variant="contained" sx={{ bgcolor: "#3f51b5"}} onClick={hendleBuyProd(props.prod)}>
          Buy
        </Button>
        {isAdmin && (
          <ButtonGroup aria-label="outlined primary button group" sx={{margin:'auto',marginRight:0,float:'right'}}>
            <IconButton>
              <Edit />
            </IconButton>
            <IconButton>
              <Delete />
            </IconButton>
          </ButtonGroup>
        )}</div>
      </CardActions>
    </Card>
     </Box>
  );
};

export default Product;
