import React, { useEffect, useState } from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { Link } from 'react-router-dom';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import './navigationBar.css'
import MenuIcon from '@mui/icons-material/Menu';

const NavigationBar = () => {
  const [isAdmin,setisAdmin]=useState(false);
  const [isLogin,setisLogin]=useState(false);

  useEffect(()=>{
    if(!isAdmin){setisAdmin(localStorage.getItem('isAdmin'))}    
    if(!isLogin){setisLogin(localStorage.getItem('isLogin'))}
  });
  
  return (
    <div className='header'>
    <AppBar position="static" sx={{bgcolor:"#3f51b5"}}>
            <Toolbar sx={{minHeight:'none'}}>
                <ShoppingCartIcon
                    size="small"
                    edge="start"
                    color="inherit"
                    aria-label="menu"
                    sx={{ mr: 1 }}
                >
                    <MenuIcon />
                </ShoppingCartIcon>
                <Typography variant="h6"
                    component="div" sx={{ flexGrow: 1 }}>
                    upGrad E-Shop
                </Typography>
                {isLogin?<>
                  <Link to="/Login">Login</Link>
                  <Link to="/SignUp">SingUp</Link></>
                 :
                <Button className='btn' variant="contained" color="inherit" sx={{bgcolor:"#f50057", m:"2"}} >Logout</Button>
              } </Toolbar>
        </AppBar>
        </div>
  );
};

export default NavigationBar;
