import { Box, Typography } from "@mui/material";
import Grid2 from "@mui/material/Unstable_Grid2/Grid2";
import React, { useState } from "react";
import './Order.css';

const ProductSummary = ({prod}) => {
  const [totalPrice,settotalPrice]=useState(0);
  const [totalQuentity,settotalQuentity]=useState(1);
  return (
    <Box sx={{ width: "75%",margin:'auto',marginTop:10}}>
      <Grid2
        container
        direction="row"
        alignItems="center"
        columns={{ xs: 4, sm: 8, md: 12 }}
      >
        <Grid2 xs={5}>
            <img className="imgProductDtl" src={prod.imgUrl}></img>
        </Grid2>
        <Grid2 xs={7}>
          <Typography variant="h5">{prod.prodName}</Typography>
          <Typography variant="h6">{prod.catagory}</Typography>
          <Typography variant="h6">{prod.Description}</Typography>
          <Typography variant="h6">{prod.Price*totalQuentity}</Typography>
          <TextField
                label="Quentity"
                variant="outlined"
                margin="normal"
                fullWidth
                size="small"
                value={Username}
                onChange={(e) => settotalPrice(e.target.value)}
            />
        </Grid2>
      </Grid2>
      </Box>
  );
};

export default ProductSummary;
