import React, { useContext, useEffect, useState } from "react";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Button from "@mui/material/Button";
import { Link, useNavigate } from "react-router-dom";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import "./navigationBar.css";
import MenuIcon from "@mui/icons-material/Menu";
import { styled, alpha } from "@mui/material/styles";
import InputBase from "@mui/material/InputBase";
import SearchIcon from "@mui/icons-material/Search";
import { AuthContext } from "./AuthContext";
import { Box, Container } from "@mui/material";

const Search = styled("div")(({ theme }) => ({
  position: "relative",
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  "&:hover": {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginRight: theme.spacing(2),
  marginLeft: 0,
  width: "100%",
  [theme.breakpoints.up("sm")]: {
    marginLeft: theme.spacing(3),
    width: "auto",
  },
}));

const SearchIconWrapper = styled("div")(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: "100%",
  position: "absolute",
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: "inherit",
  "& .MuiInputBase-input": {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create("width"),
    width: "100%",
    [theme.breakpoints.up("md")]: {
      width: "35ch",
    },
  },
}));
const NavigationBar = () => {
  const { isLoggedIn, isAdmin, login, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    let flag = localStorage.getItem("isLogin");
    if (flag === "true") {
      flag = localStorage.getItem("isAdmin");
      if (flag === "true") {
        login(true, true);
      } else {
        login(true, false);
      }
    }
  });

  const handleLogout = (e) => {
    e.preventDefault();
    localStorage.setItem("isAdmin", false);
    localStorage.setItem("isLogin", false);
    logout(false);
    navigate("/");
  };

  const [anchorElNav, setAnchorElNav] =
    (React.useState < null) | (HTMLElement > null);
  const [anchorElUser, setAnchorElUser] =
    (React.useState < null) | (HTMLElement > null);

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  return (
    <div className="header">
      <AppBar position="static" sx={{ bgcolor: "#3f51b5" }}>
        <Container maxWidth="xl">
          <Toolbar sx={{ minHeight: "none" }}>
            <ShoppingCartIcon
              size="small"
              edge="start"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 1 }}
            >
              <MenuIcon />
            </ShoppingCartIcon>
            <h5 className="title">upGrad E-Shop</h5>
            {!isLoggedIn ? (
              <div className="login">
                <Link className="title" to={"/Login"}>
                  Login
                </Link>
                <Link className="title" to={"/SignUp"}>
                  SignUp
                </Link>
              </div>
            ) : (
              <>
                <Search
                  sx={{
                    marginLeft: "-20",
                    display: { xs: "none", md: "none", lg: "flex" },
                  }}
                >
                  <SearchIconWrapper>
                    <SearchIcon />
                  </SearchIconWrapper>
                  <StyledInputBase
                    placeholder="Search…"
                    inputProps={{ "aria-label": "search" }}
                  />
                </Search>
                <Box sx={{ flexGrow: 1, display: { xs: "none", md: "flex" } }}>
                  {pages.map((page) => (
                    <Button
                      key={page}
                      onClick={handleCloseNavMenu}
                      sx={{ my: 2, color: "white", display: "block" }}
                    >
                      {page}
                    </Button>
                  ))}
                </Box>
                <div style={{ marginLeft: "auto", marginRight: "0" }}>
                  <Link className="title" to={"/"}>
                    Home
                  </Link>
                  {isAdmin && (
                    <Link className="title" to={"/AddProducts"}>
                      AddProduct
                    </Link>
                  )}
                  <Button
                    className="btn"
                    variant="contained"
                    color="inherit"
                    sx={{ bgcolor: "#f50057", m: "2" }}
                    onClick={handleLogout}
                  >
                    Logout
                  </Button>
                </div>
              </>
            )}{" "}
          </Toolbar>
        </Container>
      </AppBar>
    </div>
  );
};

export default NavigationBar;
