import React, { useContext, useEffect } from "react";
import styled from "@mui/system/styled";
import { Box, ToggleButton, ToggleButtonGroup } from "@mui/material";
import Grid2 from "@mui/material/Unstable_Grid2/Grid2";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import Product from "./Product";
import { ProdContext } from '../../common/ProductContext';


const Item = styled("div")(({ theme }) => ({
  padding: theme.spacing(1),
  textAlign: "center",
}));
const ProductsList = () => {

  const Products=[
    {productid:1,prodName:"Apple iPhone 15",catagory:"electronic",Description:"Apple iPhone 15 mobile was launched on 12th September 2023. The phone comes with a 60 Hz refresh rate 6.10-inch touchscreen display offering a resolution of 1179x2556 pixels at a pixel density of 460 pixels per inch (ppi).",price:100000,uploadDate:"",imgUrl:require("../../assets/Image/Apple15.jpg")},
    {productid:2,prodName:"Samsung 55 Inches 4K TV",catagory:"electronic",Description:"4K Ultra HD (3840 x 2160) |Refresh Rate : 50 Hertz, 3 HDMI ports to connect set top box | 1 USB ports to connect hard drives or other USB devices |Wi-fi | Bluetooth, 20W Output- 2CH | Powerful Speakers with Q-Symphony",price:64000,uploadDate:"",imgUrl:require("../../assets/Image/SamsungQled.jpg")},
    {productid:3,prodName:"Van Heusen Men T-Shirt",catagory:"apparel",Description:"The t-shirt is framed with a Crew Neck Short Sleeves: The t-shirt is designed with short sleeves.Zipper Detail On Chest: The zipper detail adds a contrasting flair to the t-shirt.",price:50000,uploadDate:"",imgUrl:require("../../assets/Image/Vtshirt.jpg")},
    {productid:4,prodName:"Vivo V29 5G",catagory:"electronic",Description:"Explore an exciting range of features on this ultra slim smartphone that features a 3D curved display. Available with smart aura light, this phone provides bright light even in low light environments.",price:32999,uploadDate:"",imgUrl:require("../../assets/Image/VivoPhone.jpg")},
    {productid:5,prodName:"NIVEA Men Cream",catagory:"personalcare",Description:"NIVEA Men CrÃ¨me, Dark Spot Reduction, Non Greasy Moisturizer, Cream with UV Protect, 75 ml & Nivea Deep Impact Freshness Deodorant for Men, 150 milliliters",price:750,uploadDate:"",imgUrl:require("../../assets/Image/NIVEAMenCream750.jpg")}
  ];

  const navigate = useNavigate();
  const [isAdmin,setIsAdmin]=useState(false);
  const { productItem,getProduct } = useContext(ProdContext);


  useEffect(() => {
    let flag = localStorage.getItem("isLogin");
    if (flag === "false") {
      navigate("/Login");
    }
    let flagisadmin = localStorage.getItem("isAdmin");
    if (flagisadmin === "true") {
      setIsAdmin(true);      
    }
    getProduct(Products)
  });


  const [alignment, setAlignment] = useState("web");
  const [filterotp,setfilterotp]=useState("");
  const [sortotp,setsortotp]=useState("");

  const handleChange = (event, newAlignment) => {
    setAlignment(newAlignment);
    setfilterotp(event.currentTarget.value);
    filterotp==="" || filterotp==="all"?
    getProduct(productItem):getProduct(productItem.filter(function (i,n){
       return i.catagory===filterotp;
   }))} 
  };

  const handleSortOptChange = (event) => {
    sortJson(productItem,event.currentTarget.value);
    

  };

  function sortJson(arr,opt){
    if(opt==="high"){
    return productItem.sort(function(a, b){
      return a.price - b.price;
    });  
  }
  else{
    return productItem.sort(function(a, b){
      return a.price - b.price;
    });  
  }

  return (
    <Box sx={{ width: "100%" }}>
        <Grid2
          container
          direction="row"
          justifyContent="center"
          alignItems="center"
          xs={11}
        >
          <Item>
            <ToggleButtonGroup
              color="primary"
              value={alignment}
              exclusive
              onChange={handleChange}
              aria-label="Platform"
            >
              <ToggleButton value="all">All</ToggleButton>
              <ToggleButton value="apparel">APPAREL</ToggleButton>
              <ToggleButton value="electronic">ELECTRONIC</ToggleButton>
              <ToggleButton value="personalcare">PERSONAL CARE</ToggleButton>
            </ToggleButtonGroup>
          </Item>
        </Grid2>
        <Grid2
          container
          direction="row"
          sx={{ml:10}}
        >
          Sort By: <select onChange={handleSortOptChange} style={{width:'250px',padding:'8px',borderRadius:'5px',marginLeft:'5px'}}>
            <option value={'0'}>Select......</option>
            <option value={'none'}>Default</option>
            <option value={'high'}>Price:High to Low</option>
            <option value={'low'}>Price:Low to High</option>
            <option value={'new'}>Newest</option>
          </select>
        </Grid2>
        
        <Grid2
          container
          direction="row"
          sx={{margin:2}}
        >
          {/* {(filterotp==="" || filterotp==="all")&&(sortotp==="")} */}
         {productItem.map((item)=>{return <Product prod={item} key={item.productid}></Product>;})} 
          {/* {          
          filterotp==="" || filterotp==="all"?
          prodList.map((item)=>{
            return <Product prod={item} key={item.productid}></Product>;
          }):prodList.filter(function (i,n){
             return i.catagory===filterotp;
         }).map((item)=>{
          return <Product prod={item} key={item.productid}></Product>;
        })}    */}
        </Grid2>            
    </Box>
  );
};

export default ProductsList;
