import React, { useState } from "react";
import { Button, TextField, Typography } from '@mui/material';
import './SignUp.css';
import LockIcon from '../../assets/Image/login.png';
import { useNavigate } from "react-router-dom";
const validateForm = () => {
    const errors = {};
    if (!firstName) {
      errors.firstName = "First Name is required";
    }
    if (!lastName) {
      errors.lastName = "Last Name is required";
    }
    if (!email) {
      errors.email = "Email Address is required";
    }
    if (!password) {
      errors.password = "Password is required";
    }
    if (password !== confirmPassword) {
      errors.confirmPassword = "Passwords do not match";
    }    if (!contactNumber) {
      errors.contactNumber = "Contact Number is required";
    }
    return errors;
  };
const SignUp=()=>{
    const navigate = useNavigate();

    // State for managing form inputs
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [contactNumber, setContactNumber] = useState("");
  
    // State for managing form validation errors
    const [errors, setErrors] = useState({});
    return (
        <div className="signup">
            <img className='imglogo' src={logo} alt=''></img>                   
                <Typography variant="h6"
                    component="div" sx={{ flexGrow: 1,textShadow:1 }}>                        
                    Sign up</Typography>
                    <div className="signup-body">
			<form onSubmit={handleSignup}>
				<div>
				<TextField
					label="First Name"
					variant="outlined"
					margin="normal"
					fullWidth
					value={firstName}
					onChange={(e) => setFirstName(e.target.value)}
				/>
				<TextField
					label="Last Name"
					variant="outlined"
					margin="normal"
					fullWidth
					value={lastName}
					onChange={(e) => setLastName(e.target.value)}
				/>
				<TextField
					label="Email"
					variant="outlined"
					margin="normal"
					fullWidth
					value={email}
					onChange={(e) => setEmail(e.target.value)}
				/>
				<TextField
					label="Password"
					type="password"
					variant="outlined"
					margin="normal"
					fullWidth
					value={password}
					onChange={(e) => setPassword(e.target.value)}
				/>
				<TextField
					label="Confirm Password"
					type="password"
					variant="outlined"
					margin="normal"
					fullWidth
					value={confirmPassword}
					onChange={(e) => setConfirmPassword(e.target.value)}
				/>
				<TextField
					label="Contact Number"
					variant="outlined"
					margin="normal"
					fullWidth
					value={contactNumber}
					onChange={(e) => setContactNumber(e.target.value)}
				/>
				<Button
					className="login__button"
					variant="contained"
					color="primary"
					onClick={handleSignup}
					fullWidth
				>
					SIGN UP
				</Button>
				</div>
			</form>
			<p>
				Already have an account? <a href="/login">Sign in</a>
			</p>
			<p style={{ textAlign:'center',padding:'50px' }}>
				Copyright ⓒ <a href="/signup">UpGrade</a> 2021
			</p>
		</div>
	</div>
    )
}

export default SignUp;
